import React, { useContext, useEffect, useState } from "react";
import { Link } from "react-router-dom";
import AppContext from "../Context/Context";

const productImages = {
  Laptop: "https://images.unsplash.com/photo-1517336714731-489689fd1ca8?auto=format&fit=crop&w=400&q=80",
  Mobile: "data:image/jpeg;base64,",
  Headphone: "data:image/jpeg;base64,",
  Electronics: "data:image/jpeg;base64,",
  Toys: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQTGcDZ4ZQJGXSBZukLTS4755Aa_LN9inlTVw&s",
  Fashion: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS-Ll_Gg0PxTbyaC5e9E36yA_CdWUFn_KPxsg&s",
  default: "data:image/jpeg;base64,",
};


const categoryBackgrounds = {
  Laptop: "data:image/jpeg;base64,",
  Mobile: "data:image/jpeg;base64,",
  Headphone: "data:image/jpeg;base64,",
  Electronics: "data:image/jpeg;base64,",
  Toys: "data:image/jpeg;base64,",
  Fashion: "data:image/jpeg;base64,",
  default: " https://media.istockphoto.com/id/868776554/photo/online-shopping-and-delivery-service-concept-paper-cartons-in-a-shopping-cart-on-a-laptop.jpg?s=612x612&w=0&k=20&c=9QeM1t0SXQeWtlKDh_TBrIx74xz09K9iZcQL7e94W-s=",
};


const Home = ({ selectedCategory }) => {
  const { data, isError, addToCart, refreshData } = useContext(AppContext);
  const [products, setProducts] = useState([]);

  // Refresh data once on mount
  useEffect(() => {
    refreshData();
  }, [refreshData]);

  // Map products to stock images
  useEffect(() => {
    if (data && data.length > 0) {
      const updatedProducts = data.map((product) => ({
        ...product,
        imageUrl: productImages[product.category] || productImages.default,
      }));
      setProducts(updatedProducts);
    }
  }, [data]);

  // Filter by category if needed
  const filteredProducts = selectedCategory
    ? products.filter((p) => p.category === selectedCategory)
    : products;

  const backgroundImage =
    categoryBackgrounds[selectedCategory] || categoryBackgrounds.default;

  // Show "no products" message if empty
  if (isError || filteredProducts.length === 0) {
    const noProductImage =
      productImages[selectedCategory] || productImages.default;
    return (
      <div
        style={{
          minHeight: "100vh",
          display: "flex",
          flexDirection: "column",
          justifyContent: "center",
          alignItems: "center",
          padding: "2rem",
          textAlign: "center",
          backgroundImage: `url(${backgroundImage})`,
          backgroundSize: "cover",
          backgroundPosition: "center",
          color: "#fff",
          textShadow: "1px 1px 6px rgba(0,0,0,0.8)",
        }}
      >
        <img
          src={noProductImage}
          alt="No products"
          style={{
            width: "250px",
            marginBottom: "20px",
            borderRadius: "12px",
            boxShadow: "0 4px 12px rgba(0,0,0,0.4)",
          }}
        />
        <h2>No Products Available</h2>
      </div>
    );
  }

  return (
    <div
      style={{
        minHeight: "100vh",
        backgroundImage: `url(${backgroundImage})`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        padding: "20px",
      }}
    >
      <div
        className="grid"
        style={{
          marginTop: "64px",
          display: "grid",
          gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))",
          gap: "20px",
          padding: "20px",
          backgroundColor: "rgba(255,255,255,0.85)",
          borderRadius: "12px",
        }}
      >
        {filteredProducts.map((product) => (
          <div
            key={product.id}
            className="card"
            style={{
              width: "250px",
              height: "360px",
              borderRadius: "10px",
              backgroundColor: product.productAvailable ? "#fff" : "#ccc",
              display: "flex",
              flexDirection: "column",
              justifyContent: "space-between",
            }}
          >
            <Link
              to={`/product/${product.id}`}
              style={{ textDecoration: "none", color: "inherit" }}
            >
              <img
                src={product.imageUrl}
                alt={product.name}
                style={{
                  width: "100%",
                  height: "150px",
                  objectFit: "cover",
                  borderRadius: "10px 10px 0 0",
                }}
              />
              <div style={{ padding: "10px" }}>
                <h5>{product.name.toUpperCase()}</h5>
                <i>~ {product.brand}</i>
                <hr />
                <h5>
                  <i className="bi bi-currency-rupee"></i>
                  {product.price}
                </h5>
                <button
                  onClick={(e) => {
                    e.preventDefault();
                    addToCart(product);
                  }}
                  disabled={!product.productAvailable}
                  style={{
                    width: "100%",
                    padding: "8px",
                    marginTop: "10px",
                    cursor: product.productAvailable ? "pointer" : "not-allowed",
                  }}
                >
                  {product.productAvailable ? "Add to Cart" : "Out of Stock"}
                </button>
              </div>
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
